           $resource[''link''] = '''';\n                }\n                $tpl = $pdoFetch->defineChunk($resource);\n                $rows_day[] = $pdoFetch->getChunk($tpl, $resource);\n                $count_year++;\n                $count_month++;\n                $count_day++;\n            }\n\n            $rows_month .= !empty($tplDay)\n                ? $pdoFetch->getChunk($tplDay, array(\n                    ''day'' => $day,\n                    ''month'' => $month,\n                    ''year'' => $year,\n                    ''count'' => $count_day,\n                    ''wrapper'' => implode($outputSeparator, $rows_day),\n                ), $pdoFetch->config[''fastMode''])\n                : implode($outputSeparator, $rows_day);\n        }\n\n        $rows_year .= !empty($tplMonth)\n            ? $pdoFetch->getChunk($tplMonth, array(\n                ''month'' => $month,\n                ''month_name'' => $modx->lexicon(''pdoarchive_month_'' . $month),\n                ''year'' => $year,\n                ''count'' => $count_month,\n                ''wrapper'' => $rows_month,\n            ), $pdoFetch->config[''fastMode''])\n            : $rows_month;\n    }\n\n    $output .= !empty($tplYear)\n        ? $pdoFetch->getChunk($tplYear, array(\n            ''year'' => $year,\n            ''count'' => $count_year,\n            ''wrapper'' => $rows_year,\n        ), $pdoFetch->config[''fastMode''])\n        : $rows_year;\n}\n$pdoFetch->addTime(''Rows processed'');\n\n// Return output\nif (!empty($tplWrapper) && (!empty($wrapIfEmpty) || !empty($output))) {\n    $output = $pdoFetch->getChunk(\n        $tplWrapper,\n        array_merge($additionalPlaceholders, array(''output'' => $output)),\n        $pdoFetch->config[''fastMode'']\n    );\n    $pdoFetch->addTime(''Rows wrapped'');\n}\n\nif ($modx->user->hasSessionContext(''mgr'') && !empty($showLog)) {\n    $output .= ''<pre class="pdoArchiveLog">'' . print_r($pdoFetch->getTime(), 1) . ''</pre>'';\n}\n\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n} else {\n    return $output;\n}', 0, 'a:36:{s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:17:"pdotools_prop_tpl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:65:"@INLINE <li>[[+date]] <a href="[[+link]]">[[+menutitle]]</a></li>";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:7:"tplYear";a:7:{s:4:"name";s:7:"tplYear";s:4:"desc";s:21:"pdotools_prop_tplYear";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:71:"@INLINE <h3>[[+year]] <sup>([[+count]])</sup></h3><ul>[[+wrapper]]</ul>";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:8:"tplMonth";a:7:{s:4:"name";s:8:"tplMonth";s:4:"desc";s:22:"pdotools_prop_tplMonth";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:86:"@INLINE <li><h4>[[+month_name]] <sup>([[+count]])</sup></h4><ul>[[+wrapper]]</ul></li>";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:6:"tplDay";a:7:{s:4:"name";s:6:"tplDay";s:4:"desc";s:20:"pdotools_prop_tplDay";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:79:"@INLINE <li><h5>[[+day]] <sup>([[+count]])</sup></h5><ul>[[+wrapper]]</ul></li>";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:10:"tplWrapper";a:7:{s:4:"name";s:10:"tplWrapper";s:4:"desc";s:24:"pdotools_prop_tplWrapper";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:11:"wrapIfEmpty";a:7:{s:4:"name";s:11:"wrapIfEmpty";s:4:"desc";s:25:"pdotools_prop_wrapIfEmpty";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:9:"dateField";a:7:{s:4:"name";s:9:"dateField";s:4:"desc";s:23:"pdotools_prop_dateField";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"createdon";s:7:"lexicon";s:19:"pdotools:properties";s:4:"area";s:0:"";}s:10:"dateFormat";a:7:{s:4:"name";s:10:"dateFormat";s:4:"desc";s:24:"pdotools_p