(29, 0, 0, 'migxChunklistToJson', '', 0, 5, 0, '$category = $modx->getOption(''category'', $scriptProperties, '''');\n$format = $modx->getOption(''format'', $scriptProperties, ''json'');\n\n$classname = ''modChunk'';\n$rows = array();\n$output = '''';\n\n$c = $modx->newQuery($classname);\n$c->select($modx->getSelectColumns($classname, $classname, '''', array(''id'', ''name'')));\n$c->sortby(''name'');\n\nif (!empty($category)) {\n    $c->where(array(''category'' => $category));\n}\n//$c->prepare();echo $c->toSql();\nif ($collection = $modx->getCollection($classname, $c)) {\n    $i = 0;\n\n    switch ($format) {\n        case ''json'':\n            foreach ($collection as $object) {\n                $row[''MIGX_id''] = (string )$i;\n                $row[''name''] = $object->get(''name'');\n                $row[''selected''] = ''0'';\n                $rows[] = $row;\n                $i++;\n            }\n            $output = $modx->toJson($rows);\n            break;\n        \n        case ''optionlist'':\n            foreach ($collection as $object) {\n                $rows[] = $object->get(''name'');\n                $i++;\n            }\n            $output = implode(''||'',$rows);      \n        break;\n            \n    }\n\n\n}\n\nreturn $output;', 0, '', '', 0, ''),
